import React from "react";
import { useState } from "react";
import Header from "./Header";
import "../css/contact.scss";

const Contact = () => {
  const [pseudo, setPseudo] = useState("");
  const [email, setEmail] = useState("");
  const [message, setMessage] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();

    const formData = {
      pseudo: pseudo,
      email: email,
      message: message,
    };

    try {
      const response = await fetch("sendMail", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(formData),
      });

      if (response.ok) {
        console.log("Email envoyé avec succès");
      } else {
        console.error("Erreur lors de l'envoi de l'email");
      }
    } catch (error) {
      console.error("Erreur lors de l'envoi de l'email", error);
    }
  };

  return (
    <div className="contactFlexContainer">
      <Header />
      <h1 className="contactFlexContainer__title">Contact</h1>
      <form className="contactFlexContainer__form" onSubmit={handleSubmit}>
        <label className="contactFlexContainer__label" htmlFor="">
          Pseudo
          <br />
          <input className="contactFlexContainer__input" type="text" required />
        </label>
        <br />
        <label className="contactFlexContainer__label" htmlFor="">
          Mail
          <br />
          <input className="contactFlexContainer__input" type="text" required />
        </label>
        <br />
        <label className="contactFlexContainer__label" htmlFor="">
          Votre message
          <br />
          <input
            className="contactFlexContainer__input"
            type="text"
            required
            value={message}
            onChange={(e) => setMessage(e.target.value)}
          />
        </label>
        <br />
        <button className="contactFlexContainer__submit" type="submit">
          Envoyer
        </button>
      </form>
    </div>
  );
};

export default Contact;
