import React from "react";
import "../css/about.scss";

const About = () => {
  return (
    <div className="aboutFlexContainer">
      <h1 className="aboutFlexContainer__title">A propos de moi</h1>
      <div className="aboutFlexContainer__boxMain">
        <p className="aboutFlexContainer__mainText">
          Salut !, <br /> Moi c'est Mero Nicolas, et je réside dans le Sud-Ouest
          de la France. Après avoir occupé plusieurs emplois alimentaires, ma
          curiosité m'a peu à peu guidé vers le monde du développement web, une
          passion naissante. Passionné de jeux vidéo, de technologie et, bien
          sûr, de développement web, je suis toujours à la recherche de
          nouvelles découvertes dans ces domaines. Je vous invite à explorer mon
          portfolio et à partager vos impressions. Vos critiques constructives
          sont les bienvenues, car elles contribuent grandement à mon évolution.
          Au plaisir de lire vos retours !
        </p>
      </div>
    </div>
  );
};

export default About;
