import React, { useState } from "react";
import Header from "./Header";
import "../css/contact.scss";

const Contact = () => {
  const [message, setMessage] = useState("");

  const handleMessageChange = (e) => {
    setMessage(e.target.value);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch("http://127.0.0.1:3001/sendMail", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ message }),
      });

      if (response.ok) {
        console.log("Message envoyé avec succès !");
      } else {
        console.error("Erreur lors de l'envoi du message.");
      }
    } catch (error) {
      console.error("Erreur lors de l'envoi du message :", error);
    }
    console.log("Message à envoyer :", message);
  };

  return (
    <div className="contactFlexContainer">
      <Header />
      <h1 className="contactFlexContainer__title">Contact</h1>
      <form className="contactFlexContainer__form" onSubmit={handleSubmit}>
        <label className="contactFlexContainer__label" htmlFor="">
          Votre message
          <br />
          <input
            className="contactFlexContainer__input"
            type="text"
            value={message}
            onChange={handleMessageChange}
            required
          />
        </label>
        <br />
        <button className="contactFlexContainer__submit" type="submit">
          Envoyer
        </button>
      </form>
    </div>
  );
};

export default Contact;
